<?phpclass Zend_Controller_Action_Helper_LoginwithGoogle extends Zend_Controller_Action_Helper_Abstract {            public $pluginLoader;    private $db = NULL;        protected $currentDateTime;    public $baseUrl;        public $_storage;        public $myNamespace;        public function __construct()    {         $this->pluginLoader = new Zend_Loader_PluginLoader();        $this->db = Zend_Db_Table::getDefaultAdapter();                $this->currentDateTime      = date('Y-m-d');            }    public function Loginwithgoogle() {        require_once __DIR__ . '/../GoogleAPI/includes/config.php';        return $gClient->createAuthUrl();        //exit;    }        public function redirectgoogle() {        require_once __DIR__ . '/../GoogleAPI/includes/config.php';                if (isset($_SESSION['access_token'])){            $gClient->setAccessToken($_SESSION['access_token']);        }else if (isset($_GET['code'])) {            $token = $gClient->fetchAccessTokenWithAuthCode($_GET['code']);            $_SESSION['access_token'] = $token;	} else {            header('Location: login');            exit();	}        //print_r($gClient);die('h');	$oAuth = new Google_Service_Oauth2($gClient);	return $userData = $oAuth->userinfo_v2_me->get();        //exit;    }}